import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './components/Header';
import ProgressBarContainer from './components/ProgressBarContainer';
import RecyclageResultats from './components/RecyclageResultats';
import './App.css';

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const [trashLevels, setTrashLevels] = useState([0, 0, 0, 0]);
  const [recyclageResultats, setRecyclageResultats] = useState('');

  useEffect(() => {
    // Utilisez Axios pour récupérer les valeurs des trashLevels depuis le serveur Flask
    axios.all([
      axios.post('http://localhost:5000/show_distance1'),
      axios.post('http://localhost:5000/show_distance2'),
      axios.post('http://localhost:5000/show_distance3'),
      axios.post('http://localhost:5000/show_distance4')
    ])
    .then(axios.spread((res1, res2, res3, res4) => {
      const distance1 = res1.data.distance1;
      const distance2 = res2.data.distance2;
      const distance3 = res3.data.distance3;
      const distance4 = res4.data.distance4;
      
      setTrashLevels([distance1, distance2, distance3, distance4]);
    }))
    .catch(error => {
      console.error('Erreur lors de la récupération des données:', error);
    });
  }, []);  // Utilisez une dépendance vide pour ne déclencher cet effet qu'une fois lors du montage du composant

  return (
    <>
      <Header currentDateTime={currentDateTime} />
      <RecyclageResultats recyclageResultats={recyclageResultats} />
      <ProgressBarContainer trashLevels={trashLevels} />
    </>
  );
}

export default App;
