import React, { useState, useEffect } from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'; // Import de la ProgressBar de React Bootstrap
import './App.css'; // Import du fichier CSS séparé

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Tableau représentant les niveaux de remplissage des poubelles (en pourcentage)
  const trashLevels = [25, 50, 75, 100];

  return (
    <div className="app-container">
      <div className="logo-container">
        <img src="AI_SORTER_LOGO.png" alt="Logo de votre projet" className="logo" />
      </div>
      <div className="date-time-container">
        <div className="time">
          {currentDateTime.toLocaleTimeString('fr-FR')}
        </div>
        <div className="date">
          {currentDateTime.toLocaleDateString('fr-FR')}
        </div>
      </div>
      <div className="trash-bins-container">
        {trashLevels.map((level, index) => (
          <div key={index} className="trash-bin">
            <i className="fas fa-trash trash-icon"></i>
            <ProgressBar now={level} label={`${level}%`} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
