// import './App.css';
import React, { useState, useEffect } from 'react';
import { library } from '@fortawesome/fontawesome-svg-core';
import { fas } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import ProgressBar from 'react-bootstrap/ProgressBar';

library.add(fas);

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  const trashLevels = [25, 50, 75, 100]; 

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <><div style={containerStyle}>
      <div style={logoContainerStyle}>
        <img src="AI SORTER LOGO.png" alt="Logo de votre projet" style={logoStyle} />
      </div>
      <div style={dateTimeStyle}>
        <div style={timeStyle}>
          {currentDateTime.toLocaleTimeString('fr-FR')}
        </div>
        <div style={dateStyle}>
          {currentDateTime.toLocaleDateString('fr-FR')}
        </div>
      </div>
    </div>

    <div style={{ display: 'flex', justifyContent: 'space-around', alignItems: 'center' }}>
      {trashLevels.map((level, index) => (
        <div key={index} style={{ textAlign: 'center', margin: '10px' }}>
          <i className="fas fa-trash" style={{ fontSize: '48px' }}></i> {/* Ic√¥ne de poubelle */}
          <ProgressBar now={level} label={`${level}%`} /> {/* Barre de progression pour le niveau de remplissage */}
        </div>
      ))}
    </div></>
  );
}

const containerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '7px',
  backgroundColor: '#f0f0f0',
};

const logoContainerStyle = {
  marginRight: 'auto',
};

const logoStyle = {
  maxWidth: '100px',
  height: 'auto',
};

const dateTimeStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'flex-end',
};

const timeStyle = {
  fontSize: '20px',
  marginBottom: '5px',
};

const dateStyle = {
  fontSize: '16px',
};

export default App;
