// import './App.css';
import React, { useState, useEffect } from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar';

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <><div style={containerStyle}>
      <div style={logoContainerStyle}>
        <img src="AI SORTER LOGO.png" alt="Logo de votre projet" style={logoStyle} />
      </div>
      <div style={dateTimeStyle}>
        <div style={timeStyle}>
          {currentDateTime.toLocaleTimeString('fr-FR')}
        </div>
        <div style={dateStyle}>
          {currentDateTime.toLocaleDateString('fr-FR')}
        </div>
      </div>
    </div>

    <div className="container">
  <div className="progress progress-bar-vertical">
    <div className="progress-bar" role="progressbar" aria-valuenow="30" aria-valuemin="0" aria-valuemax="100" style={{ height: '30%' }}>
      <span className="sr-only">30% Complete</span>
    </div>
  </div>

  <div className="progress progress-bar-vertical">
    <div className="progress-bar progress-bar-danger progress-bar-striped" role="progressbar" aria-valuenow="60" aria-valuemin="0" aria-valuemax="100" style={{ height: '60%' }}>
      <span className="sr-only">60% Complete</span>
    </div>
  </div>

  <div className="progress progress-bar-vertical">
    <div className="progress-bar progress-bar-success progress-bar-striped active" role="progressbar" aria-valuenow="100" aria-valuemin="0" aria-valuemax="100" style={{ height: '100%' }}>
      <span className="sr-only">100% Complete</span>
    </div>
  </div>
</div></>
  );
}

const containerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '7px',
  backgroundColor: '#f0f0f0',
};

const logoContainerStyle = {
  marginRight: 'auto',
};

const logoStyle = {
  maxWidth: '100px',
  height: 'auto',
};

const dateTimeStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'flex-end',
};

const timeStyle = {
  fontSize: '20px',
  marginBottom: '5px',
};

const dateStyle = {
  fontSize: '16px',
};

export default App;
